import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import logo from "../assets/logo.png";

const StyledLandingPage = styled.div`
	margin-top: 8%;
	font-family: "Nunito", sans-serif;

	.main-section {
		display: flex;
		justify-content: space-between;
		width: 100%;
	}

	.section-heading {
		font-size: 2.5rem;
	}

	.main-left {
		padding-left: 5%;
		display: flex;
		flex-direction: column;
		justify-content: center;
		width: 50%;
	}

	.call-to-action-btns {
		margin-top: 5%;
		display: flex;
	}

	.cta-btn {
		background-color: #8ebd40;
		border-radius: 15px;
		display: flex;
		justify-content: center;
		width: 30%;
		margin-right: 10%;
	}

	.learn-more {
		background-color: #fff;
		border: 2px solid #8ebd40;
	}

	.btn {
		font-family: "Amatic SC", cursive;
		color: white;
		text-decoration: none;
		text-align: center;
		font-size: 2rem;
		padding: 8% 0;
		width: 100%;
	}

	.learn-more-btn {
		color: #8ebd40;
	}

	.main-right {
		display: flex;
		justify-content: center;
	}

	.logo {
		width: 50%;
	}

	.about-section {
		margin-top: 10%;
		display: flex;
		width: 100%;
		justify-content: space-between;
	}

	.about-left {
		width: 50%;
	}

	.profile {
		width: auto;
	}

	.profile-img {
		width: 30%;
		border-radius: 50%;
	}

	.about-right {
		width: 50%;
	}
`;

const LandingPage = () => {
	return (
		<StyledLandingPage>
			<section className="main-section">
				<div className="main-left">
					<h2 className="section-heading">
						Debt management solution helping users effectively pay
						down their debt
					</h2>
					<p style={{ fontSize: "1.2rem" }}>
						Billy's algorithm establishes and routinely optimizes a
						user's financial plan in order to make sure their
						payments are maximizing their interest savings.{" "}
					</p>
					<div className="call-to-action-btns">
						<div className="cta-btn">
							<NavLink className="btn" to="/">
								Demo
							</NavLink>
						</div>
						<div className="cta-btn learn-more">
							<NavLink className="btn learn-more-btn" to="/">
								Learn More
							</NavLink>
						</div>
					</div>
				</div>
				<div className="main-right">
					<img className="logo" src={logo} alt="Billy logo" />
				</div>
			</section>

			<section className="about-section">
				<div className="about-left">
					<div className="profile">
						<img
							className="profile-img"
							src="https://media-exp1.licdn.com/dms/image/C4D03AQELgNHQ2Iwcag/profile-displayphoto-shrink_800_800/0/1582760083283?e=1624492800&v=beta&t=1FMVw-4oAmOY0LhDvmPtM4958NTMMVUnuS5i0A3bJlA"
							alt="Daniela"
						/>
					</div>
					<div className="profile">
						<img
							className="profile-img"
							src="https://media-exp1.licdn.com/dms/image/C4D03AQEqDj2ucdnQcg/profile-displayphoto-shrink_800_800/0/1589453572566?e=1624492800&v=beta&t=Fiplm0dZ1DZr2rFtb0zbB2IUffJYOkjCv1F2eSeP0eM"
							alt="George"
						/>
					</div>
				</div>
				<div className="about-right">
					<h3 className="section-heading">Meet the Team</h3>
					<p>About us</p>
				</div>
			</section>
		</StyledLandingPage>
	);
};

export default LandingPage;
