import React from "react";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import logo from "../assets/logo.png";

const StyledLandingPage = styled.div`
	margin-top: 8%;
	font-family: Ubuntu, sans-serif;

	.main-section {
		display: flex;
		justify-content: space-between;
		width: 100%;
	}

	.main-heading {
		margin-top: 0;
	}

	.main-left {
		padding-left: 5%;
		display: flex;
		flex-direction: column;
		justify-content: center;
		width: 50%;
	}

	.call-to-action-btns {
		margin-top: 5%;
		display: flex;
	}

	.cta-btn {
		background-color: #8ebd40;
		border-radius: 15px;
		display: flex;
		justify-content: center;
		width: 35%;
		margin-right: 5%;
	}

	.btn {
		font-family: "Amatic SC", cursive;
		color: white;
		text-decoration: none;
		text-align: center;
		font-size: 1.5rem;
		padding: 10% 10%;
		width: 100%;
	}

	.main-right {
		display: flex;
		justify-content: center;
	}

	.logo {
		width: 40%;
	}
`;

const LandingPage = () => {
	return (
		<StyledLandingPage>
			<section className="main-section">
				<div className="main-left">
					<h2 className="main-heading">
						Debt management solution helping users effectively pay
						down their debt
					</h2>
					<p>
						Billy's algorithm establishes and routinely optimizes a
						user's financial plan in order to make sure their
						payments are maximizing their interest savings.{" "}
					</p>
					<div className="call-to-action-btns">
						<div className="cta-btn">
							<NavLink className="btn" to="/">
								Demo
							</NavLink>
						</div>
						<div className="cta-btn">
							<NavLink className="btn" to="/">
								Learn More
							</NavLink>
						</div>
					</div>
				</div>
				<div className="main-right">
					<img className="logo" src={logo} alt="Billy logo" />
				</div>
			</section>

			<section>
				<div>
					<h3>Meet the Team</h3>
					<p>About us</p>
				</div>
				<div>
					<img src="/" alt="George and Daniela" />
				</div>
			</section>
		</StyledLandingPage>
	);
};

export default LandingPage;
